<script>
import { FORM_MODE } from '@/app-constant'
import { mapState, mapActions } from 'vuex'
import TagList from '@/portal-components/tag/tag-list'

export default {
  components: {
    TagList
  },
  name: 'AppSidebar',
  computed: mapState(['navbar', 'documentCount', 'tags']),
  data () {
    return {
      filterTags: []
    }
  },
  mounted () {
    this.countDocument()
    this.getTags()
      .then(() => {
        this.filterResetTag()
      })
    this.getGroups()
  },
  methods: {
    ...mapActions(['toggleNavbar', 'activeNavbarGroup', 'toggleNavbarGroupItems', 'countDocument', 'getTags', 'getGroups']),
    onClickCreateDocument () {
      this.$modal.show('document-upload-modal', { model: { tag_id: [] }, mode: FORM_MODE.UPLOAD_NEW_DOC })
    },
    onToggleNavbarGroupItems (group, $event) {
      $event.stopPropagation()
      this.toggleNavbarGroupItems(group)
    },
    onClickAddTag () {
      this.$modal.show('tag-modal', { model: {} })
    },
    filterSetTag (filterText) {
      this.filterTags = this.tags.filter(tag => tag.text.toLowerCase().includes(filterText))
    },
    filterResetTag () {
      this.filterTags = this.tags
    }

  },
  watch: {
    $route (to) {
      this.activeNavbarGroup(to.meta.group)
    }
  }
}
</script>

